--
CREATE OR REPLACE 
	DEFINER = 'root'@'localhost'
VIEW meetingsview
AS
SELECT
  `meetings`.`meetingname` AS `meetingname`,
  `groups`.`groupname` AS `groupname`,
  `meetingday`.`meetingday` AS `meetingday`,
  `meetingfrequency`.`meetingfrequency` AS `meetingfrequency`,
  `meetingstart`.`meetingstart` AS `meetingstart`,
  `meetingend`.`meetingend` AS `meetingend`,
  `locations`.`locationname` AS `locationname`,
  `locationtypes`.`locationtype` AS `locationtype`,
  `locations`.`locationaddress` AS `locationaddress`,
  `locations`.`locationcity` AS `locationcity`,
  `locations`.`locationstate` AS `locationstate`,
  `locations`.`locationzip` AS `locationzip`,
  `districts`.`district` AS `district`,
  `districts`.`districtdescription` AS `districtdescription`,
  `areas`.`areaname` AS `areaname`,
  `areas`.`areadescription` AS `areadescription`,
  `locations`.`locationlat` AS `locationlat`,
  `locations`.`locationlng` AS `locationlng`
FROM (((((((((`groups`
  JOIN `meetings`
    ON ((`groups`.`groupsindex` = `meetings`.`groupid`)))
  JOIN `meetingday`
    ON ((`meetings`.`meetingdayid` = `meetingday`.`meetingdayindex`)))
  JOIN `meetingfrequency`
    ON ((`meetings`.`meetingfrequencyid` = `meetingfrequency`.`meetingfrequencyindex`)))
  JOIN `meetingstart`
    ON ((`meetings`.`meetingstartid` = `meetingstart`.`meetingstartindex`)))
  JOIN `districts`
    ON ((`groups`.`districtindex` = `districts`.`districtsindex`)))
  JOIN `areas`
    ON ((`districts`.`areaindex` = `areas`.`areasindex`)))
  JOIN `locations`
    ON ((`meetings`.`meetinglocationid` = `locations`.`locationsindex`)))
  JOIN `locationtypes`
    ON ((`locations`.`locationtypeid` = `locationtypes`.`locationtypesindex`)))
  JOIN `meetingend`
    ON ((`meetings`.`meetingendid` = `meetingend`.`meetingendindex`)))
WHERE (`meetings`.`active` = 1)
GROUP BY `locations`.`locationcity`,
         `locations`.`locationzip`,
         `meetingday`.`meetingday`,
         `meetingstart`.`meetingstart`,
         `locations`.`locationaddress`,
         `groups`.`groupname`,
         `meetings`.`meetingname`
