INSERT INTO committees VALUES
(2, 'Accessibility Committee', 3, 'This committee will handle all accessibility needs and issues for Western Washington Area. Along with funding to attend Area Quarterlies and the Assembly, this position has additional funds to provide interpretation for Area events. ', NULL, '2016-05-09 16:47:48', 1, 1),
(3, 'Archives Standing Committee', 3, 'The committee consists of the Area Archivist, the Archives Steering Committee, the District and other A.A. archivists. The Area Archivist and the Archives Steering Committee represent the interests of the local Archivists to the Area Committee and to the Assembly, although only the Archivist is a member of the Area Committee.', NULL, '2016-05-09 16:48:11', 1, 1),
(4, 'Archives Steering Committee', 3, 'The Archives Steering Committee consists of six members, each serving a four-year term. In each odd-numbered year, three members will be replaced. This insures continuity of experience on the Steering Committee. The seventh member of the Steering Committee is the Area Archivist, who acts as chairperson of the committee.', NULL, '2016-05-09 16:48:28', 1, 1),
(5, 'Cooperation with the Professional Community Committee', 3, 'Cooperation with the Professional Community (C.P.C.) is A.A.''s official contact with members of the professional community. If you are sober and have a desire to serve, we need your help! ', NULL, '2016-05-09 16:48:36', 1, 1),
(6, 'Corrections Committee', 3, 'The Area 72 Corrections Committee has one purpose: To help A.A. members carry the message of Alcoholics Anonymous to the alcoholic who is confined in a correctional facility. If you are sober and have a desire to serve, we need your help!', NULL, '2016-05-09 16:48:48', 1, 1),
(7, 'Grapevine and Literature Committee', 3, 'The Grapevine and Literature Committee follows the guidelines as outlined in the Grapevine Workbook and in the Literature Committee Guidelines marked MG-9 from G.S.O.  Exists primarily to educate the fellowship about A.A. literature, related service material, and how to obtain it. Maintains a comprehensive literature display consisting of items from A.A.W.S., G.S.O., A.A. Grapevine, Inc., and other A.A.', NULL, '2016-05-09 16:49:05', 1, 1),
(8, 'Public Information Committee', 3, 'Public Information (P.I.) in Alcoholics Anonymous (A.A.) means carrying the message of recovery to the still suffering alcoholic by informing the general public about the A.A. program.  If you are sober and have a desire to serve, you can help! When they''ve had enough, we want A.A. to be there in the yellow pages, on the bus signs, etc. - and that''s Public Information.', NULL, '2016-05-09 16:49:21', 1, 1),
(9, 'Treatment Facilities Committee', 3, 'The Area 72 Treatment Facilities Committee has one purpose: To help A.A. members carry the message of Alcoholics Anonymous into Treatment Facilities, and help "Bridge the Gap" between Facilities and A.A.  If you are sober and have a desire to serve, we need your help!', NULL, '2016-05-09 16:49:34', 1, 1),
(10, 'Web Standing Committee', 3, 'Our mission is to promote unity through application of the Twelve Traditions while working with modern technology to carry the AA message to the alcoholic who still suffers. ', NULL, '2016-05-09 16:49:46', 1, 1),
(11, 'Web Steering Committee', 3, 'The Area Web Steering Committee is created per motion 04.2 which states:  "The Area Website Committee be composed of the appointed Area Webmaster, the appointed  Area Public Information Chair and a steering committee of three volunteers with a demonstrated interest in the Traditions, general service and/or Websites.  The Committee will maintain and further develop the area Website, following policies set forth by the original Website committee to ensure that the traditions are upheld."', NULL, '2016-05-09 16:50:09', 1, 1),
(32, 'Other Committee', 3, 'New?  Please ask to have it added to the list.', NULL, '2016-05-09 16:50:29', 1, 1);

